list *search_list(list *my_list, data_type x) {
    if (my_list == NULL) {
        return(NULL);
    }

    // If `x` is in the list, it's either the first element or located in the rest of the list.
    if (my_list->data == x) {
        return(my_list);
    } else {
        return(search_list(my_list.next, x));
    }
}
