data class Element(
    val num: Int,
    internal val minNumSoFar: Int
)

class Stack {

    private val list = mutableListOf<Element>()

    fun push(num: Int) {
        list += Element(
            num = num,
            minNumSoFar = Math.min(num, list.lastOrNull()?.minNumSoFar ?: num)
        )    
    }

    fun pop(): Int {
        return list.removeLast().num
    }

    fun findMin(): Int {
        return list.last().minNumSoFar
    }
}
