fun deleteInConstantTime(head: Node, search: Node): Node? {
    if (head == search) {
        // Handle head case
        return head.next
    }

    var node: Node? = head
    var prevNode: Node? = null
    while (node != null) {
        if (node == search) {
            if (node.next == null) {
                // Handle tail case
                prevNode?.next = null
            } else {
                node.element = node.next!!.element
                node.next = node.next?.next
            }

            break
        } else {
            prevNode = node
            node = node.next
        }
    }

    return head
}

