fun isAnagram(s1: String, s2: String): Boolean {
    val map = mutableMapOf<Char, Int>()

    s1.forEach { char ->
        map[char] = map.getOrPut(char) { 0 } + 1
    }

    s2.forEach { char ->
        if (map.containsKey(char)) {
            map[char] = map.getValue(char) - 1
        } else {
            return false
        }
    }

    map.values.forEach { number ->
        if (number != 0) {
            return false
        }
    }

    return true
}
