fun concat(s1: BNode, s2: BNode): BNode {
    val s1RightMostNode = rightMostNode(s1)

    s1RightMostNode.right = s2

    return s1
}

fun rightMostNode(node: BNode): BNode {
    if (node.right == null) {
        return node
    }

    return rightMostNode(node.right!!)
}

