fun containsAllCharacters(string: String, magazine: String): Boolean {
    val map = mutableMapOf<Char, Int>()

    string.forEach { char ->
        map[char] = map.getOrDefault(char, 0) + 1
    }

    var matched = 0
    magazine.forEach { char ->
        val count = map[char] ?: 0

        if (count > 0) {
            matched++
            map[char] = count - 1
        }

        if (matched == string.length) {
            return true
        }
    }

    return false
}
