fun reverse(sentence: String): String {
    val words = sentence.split(" ")
    var reversedSentence = ""

    var i = words.lastIndex
    while (i >= 0) {
        val word = words[i]
        reversedSentence += word
        i--

        if (i >= 0) {
            reversedSentence += " "
        }
    }

    return reversedSentence
}
