fun detectLoopLocation(head: Node): Node? {
    var hare: Node? = head
    var tortoise: Node? = head

    while (hare != null) {
        hare = hare.next?.next
        tortoise = tortoise?.next

        if (hare == tortoise) {
            break
        }
    }

    if (head.next != null && hare == tortoise) {
        hare = head
        while (hare != tortoise) {
            hare = hare?.next
            tortoise = tortoise?.next
        }

        return tortoise!!
    }

    return null
